mapper.update:
update bora_notice
set n_title=#{n_title}, n_content=#{n_content}, n_file=#{n_file}
where nno=#{nno}

mapper.select:
select * from bora_notice
order by n_regdate desc
limit #{pageStart}, #{perPageNum}

mapper.update:
update bora_notice
set n_readcount = n_readcount+1
where nno=#{nno}
